-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `medicine`
--

CREATE TABLE `medicine` (
  `id` int(255) NOT NULL,
  `clinicId` int(255) DEFAULT NULL,
  `unitId` int(255) DEFAULT NULL,
  `registerCode` varchar(255) DEFAULT NULL,
  `standard` varchar(255) NOT NULL,
  `expectancy` varchar(255) NOT NULL,
  `concentration` float NOT NULL,
  `madeIn` varchar(255) NOT NULL,
  `note` longtext,
  `active` tinyint(1) NOT NULL DEFAULT '1',
  `price` int(255) NOT NULL,
  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `updatedAt` timestamp NULL DEFAULT NULL,
  `name` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `medicine`
--

INSERT INTO `medicine` (`id`, `clinicId`, `unitId`, `registerCode`, `standard`, `expectancy`, `concentration`, `madeIn`, `note`, `active`, `price`, `createdAt`, `updatedAt`, `name`) VALUES
(1, 1, 1, 'VD-22087-15', 'TCCS', '36', 0.025, 'Việt Nam', 'Khi dùng Fluocinolon Acetonid trên mảng da rộng không nên băng kín vì tăng nguy cơ nhiễm độc toàn thân. Có thể gây suy vỏ tuyến thượng thận ở những người bệnh dùng lượng lớn thuốc và bôi trên diện rộng, dài ngày hoặc băng kín.  Những người bị vảy nến cần ', 1, 250000, '2017-11-24 07:39:22', '0000-00-00 00:00:00', 'Fresma'),
(2, 1, 1, 'VD-19258-13', 'TCCS', '24', 0.025, 'Việt Nam', 'Người lớn, trẻ > 12 tuổi: Bôi lớp mỏng lên vùng bị đau, xoa kỹ, 3 – 4 lần/ngày, 2 – 4 tuần\r\nĐau khớp: Từ 1 – 2 tuần\r\nĐau thần kinh: Từ 2 – 4 tuần (trường hợp đau ở đầu và cổ có thể dùng từ 4 – 6 tuần)', 1, 150000, '2017-11-24 07:39:52', '0000-00-00 00:00:00', 'Capsicin gel'),
(3, 1, 1, 'VN-9859-10', 'NSX', '24', 0.05, 'Singapore', '1. Loại thuốc:\r\n\r\nThuốc điều trị bệnh da liễu\r\n\r\n2. Dạng thuốc:\r\n\r\nViên nang mềm\r\n\r\n3. Dược lý và Cơ chế tác dụng:\r\n\r\nIsotretinoin còn được gọi là 13-cis Retinoic acid, là dược chất dùng để chữa mụn trứng cá, làm giảm bài tiết và kích thích tuyến bã nhờn ', 1, 165000, '2017-11-24 07:40:19', '0000-00-00 00:00:00', 'Isotrex Cream'),
(4, 1, 1, 'VD-21616-14', 'TCCS', '36 tháng', 0.1, 'Việt Nam', 'Tacrolimus được sử dụng để ngăn chặn sự đào thải (hệ thống miễn dịch tấn công các cơ quan được cấy ghép) ở những người đã được cấy ghép thận, gan hay tim.\r\n\r\nNó có thể được dùng kết hợp với Steroid, Azathioprine (Imuran Azasan) hoặc Mycophenolate Mofetil.', 1, 322000, '2017-11-24 07:40:58', '0000-00-00 00:00:00', 'Sovalimus'),
(5, 1, 3, 'VD-13503-10', 'DĐVN IV', '24', 0, 'Việt Nam', 'Azithromycin được chỉ định dùng trong các trường hợp nhiễm khuẩn do các vi khuẩn nhạy cảm với thuốc như nhiễm khuẩn đường hô hấp dưới bao gồm viêm phế quản, viêm phổi, các nhiễm khuẩn da và mô mềm, viêm tai giữa, nhiễm khuẩn đường hô hấp trên như viêm xoang, viêm họng và viêm Amidan. Azithromycin chỉ nên dùng cho những người bệnh dị ứng với Penicilin, vì nguy cơ kháng thuốc.  Trong những bệnh lây nhiễm qua đường tình dục ở cả nam và nữ, Azithromycin được dùng điều trị nhiễm khuẩn đường sinh dục chưa biến chứng do Chlamydia Trachomatis hoặc Neisseria Gonorrhoeae không đa kháng.', 1, 380000, '2017-11-24 07:41:11', '0000-00-00 00:00:00', 'Azithromycin TW3 '),
(6, 1, 3, 'VD-20045-13', 'TCCS', '2017-11-24', 0.25, 'Việt Nam', 'Loãng xương sau mãn kinh.\r\n\r\nLoạn dưỡng xương do thận ở bệnh nhân bị suy thận mạn, đặc biệt ở bệnh nhân làm thẩm phân máu.\r\n\r\nThiểu năng tuyến cận giáp sau phẫu thuật.\r\n\r\nThiểu năng tuyến cận giáp nguyên phát.\r\n\r\nThiểu năng tuyến cận giáp giả.\r\n\r\nCòi xương đáp ứng với Vitamin D.\r\n\r\nCòi xương kháng với Vitamin D, kèm theo giảm Phosphate máu.', 1, 650000, '2017-11-24 07:41:28', NULL, 'Dofosca');

