--
-- Cấu trúc bảng cho bảng `post`
--

CREATE TABLE `post` (
  `id` int(255) NOT NULL,
  `userId` int(255) DEFAULT NULL,
  `categoryId` int(255) DEFAULT NULL,
  `name` varchar(255) NOT NULL,
  `avatar` varchar(255) DEFAULT NULL,
  `content` text,
  `views` int(255) NOT NULL DEFAULT '0',
  `unsigned_name` varchar(255) DEFAULT NULL,
  `createdAt` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `post`
--

INSERT INTO `post` (`id`, `userId`, `categoryId`, `name`, `avatar`, `content`, `views`, `unsigned_name`, `createdAt`) VALUES
(1, 3, 1, '5 lưu ý khi trẻ bị viêm họng, mẹ không nên chủ quan', 'post-1236.png', '<p><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">Thời tiết thất thường, cùng với ô nhiễm môi trường sẽ khiến bé dễ gặp các vấn đề về sức khỏe, nhất là các bệnh về đường hô hấp. Trong đó,</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;viêm họng</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;là bệnh dễ mắc nhất ở trẻ nhỏ, tuy không nguy hiểm, nhưng nếu không chữa trị kịp thời sẽ gây hậu quả về sau như viêm amidan, viêm phổi, viêm phế quản.</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Để giúp các mẹ hiểu rõ về căn bệnh này,&nbsp;<b>ViCare</b>&nbsp;sẽ cung cấp&nbsp;<b>5 lưu ý khi trẻ bị viêm họng, mẹ không nên chủ quan</b>, những thông tin quan trọng này sẽ giúp các mẹ dễ dàng nắm bắt căn bệnh mùa đông thường gặp ở trẻ..</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">1. Nguyên nhân gây viêm họng ở trẻ mẹ nên biết</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Có hai nhóm nguyên nhân chính gây viêm họng cho bé đó là:</p><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Cảm lạnh, virus cúm, sởi, rhino, adeno</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Vi khuẩn tụ cầu, liên cầu, phế cầu.</p></li></ul><p><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">Viêm họng</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;ở trẻ em được coi là nguy hiểm khi nguyên nhân là do liên cầu beta huyết nhóm A gây nên. Vỏ của loại liên cầu này có cấu trúc gần giống cấu trúc của màng tim, màng khớp, màng thận.&nbsp;</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">Trẻ bị viêm họng</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;do loại liên cầu này gây nên nếu không được chữa trị kịp thời, cơ thể sẽ sản sinh các kháng thể chống lại liên cầu này đồng thời tấn công vào tim, thận, khớp, gây nên những biến chứng nặng nề cho trẻ</span></p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171216_062236_362872_pasted_image_0.max-1800x1800.png\" alt=\"vicare.vn-5-luu-y-khi-tre-bi-viem-hong-me-khong-nen-chu-quan-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">2. Trẻ bị viêm họng có những triệu chứng gì?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Rất dễ dàng nhận thấy trẻ có triệu chứng viêm họng, mẹ cần để ý khi bé có những biểu hiện sau đây để có những phương pháp điều trị viêm họng kịp thời.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Triệu chứng các mẹ có thể thấy được, bao gồm</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\">Thường sốt cao trên 38.5 độ C.</li><li style=\"margin: 0px 0px 10px;\">Hay quấy khóc, chán ăn, bú kém, cần nhận biết thêm những triệu chứng khác để phân biệt bé viêm họng và khi bé mọc răng, vì bé mới mọc răng sữa cũng quấy khóc và biếng ăn.<br></li></ul><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Triệu chứng phát hiện khi đi khám</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\">Xuất hiện mủ trắng bẩn ở khe hốc amidan hai bên.</li><li style=\"margin: 0px 0px 10px;\">Xuất hiện hạch hai bên hàm, ấn vào thấy đau.</li><li style=\"margin: 0px 0px 10px;\">Xét nghiệm máu thấy bạch cầu đa nhân trung tính tăng cao.<br></li></ul><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">3. Các dấu hiệu viêm họng mẹ cần cho bé đi khám bác sĩ ngay</h2><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Với những bé bị viêm họng biểu hiện: sốt cao, nhịp thở nhanh, chảy dãi nhiều thì nên đưa bé đi khám. Bé dưới 3 tháng tuổi, đưa bé đi khám ngay nếu bé bị sốt trên 38 độ hoặc hơn. Bé khoảng 3-6 tháng tuổi , sốt đến khoảng 38,3 độ là nghiêm trọng. Bé trên 6 tháng tuổi sốt trên 39 độ C là cảnh báo.</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Nên đưa bé đi khám sớm nếu cổ họng bất thường như sưng (tấy) đỏ. Nghi ngờ bé nuốt phải dị vật ( bé không thể mở miệng to vì đau; hơi thở khó nhọc; kém ăn (lười bú), quấy khóc liên tục.</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Trường hợp nhập viện khẩn cấp thường khá hiếm. Đó là trường hợp bé bị viêm họng dạng nhiễm khuẩn cổ họng tới mức không thể ăn, uống bất cứ thứ gì; mở to miệng để kiểm tra. Đừng nên ép bé ăn uống vì làm như vậy chỉ khiến bé khó thở hơn. Tốt nhất nên đưa bé đi khám sớm.</p></li></ul><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171216_062324_492547_1_73916.max-1800x1800.jpg\" alt=\"vicare.vn-5-luu-y-khi-tre-bi-viem-hong-me-khong-nen-chu-quan-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">4. Điều trị viêm họng cho bé</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Phần lớn các trường hợp gây bệnh viêm họng và viêm đường hô hấp trên ở trẻ đều do virus, khi bệnh ở mức độ nhẹ, các mẹ chớ nên vội cho con uống kháng sinh, việc uống thuốc lúc này không cần thiết và có thể gây nên tình trạng “nhờn thuốc” ở trẻ.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Các mẹ có thể tham khảo một số bài thuốc dân gian phù hợp với độ tuổi và tính chất bệnh để&nbsp;<b>chữa viêm họng cho bé</b>.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đồng thời, mẹ nên kết hợp các biện pháp sau đây để điều trị viêm họng cho bé tại nhà:</p><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Đối với trẻ vào tuổi ăn dặm (từ 5-6 tháng tuổi), mẹ có thể cho bé uống đồ ấm, trà hoặc nước luộc rau để làm dịu cổ họng cho bé. Cho bé uống nhiều nước lọc hoặc có thể thay thế bằng nước hoa quả. Các mẹ lưu ý không được sử dụng mật ong để điều trị viêm họng cho trẻ dưới 12 tháng tuổi vì nó có độc tố khiến hệ tiêu hóa của bé trong độ tuổi này ảnh hưởng.</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Thực đơn đồ ăn cho bé cần nghiền nhỏ hơn bình thường, cháo cần nấu loãng hơn để bé dễ nuốt</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Đối với trẻ sơ sinh cần cho bé bú nhiều hơn bình thường, nếu bé bị đau họng mà bú ít, mẹ có thể giảm lượng sữa bú mỗi lần và tăng số lần bú trong ngày nhiều lên.</p></li></ul><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171216_062344_197005_image2_2.max-1800x1800.jpg\" alt=\"vicare.vn-5-luu-y-khi-tre-bi-viem-hong-me-khong-nen-chu-quan-body-3\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">5. Cách phòng tránh bệnh viêm họng ở trẻ</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Để phòng tránh bệnh viêm họng ở trẻ, các mẹ nên:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Hạn chế cho trẻ đến chỗ đông người, nhớ đeo khẩu trang và che chắn cẩn thận khi bé ra ngoài vào những ngày nắng nóng</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">-Mùa hè không nên cho bé ăn nhiều thức ăn lạnh như kem</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Tập cho bé thói quen uống nhiều nước, hoạt động thể chất, đánh răng sau mỗi bữa ăn, rửa tay trước khi ăn và sau khi đi vệ sinh.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Trên đây là 5 lưu ý khi<b>&nbsp;trẻ bị viêm họng&nbsp;</b>mà&nbsp;<b>ViCare&nbsp;</b>muốn chia sẻ với các mẹ để dễ nắm bắt và chủ động hơn trong việc phòng và chữa trị viêm họng cho bé.</p><p><div class=\"streamfield cms\" style=\"float: none; width: 753.328px;\"><div class=\"block-content\"><div class=\"rich-text\"><p style=\"margin-top: 0px; margin-bottom: 0px;\"></p></div></div></div></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Chúc các mẹ thành công và thông thái hơn trong vai trò cao cả!</p>', 12, NULL, '2018-01-10 15:08:16'),
(2, 9, 1, 'Cách điều trị và phòng ngừa viêm VA?', 'post-1234.jpg', '<div style=\"color: rgb(51, 51, 51); font-size: 14px;\"><div class=\"rich-text\"><p style=\"margin-top: 0px; margin-bottom: 0px;\">Bệnh viêm VA là một trong những bệnh trẻ em thường gặp vào mùa đông.&nbsp;<b>Viêm VA</b>&nbsp;do bị viêm nhiễm khi nó bị tái phát nhiều lần và trở thành ổ vi khuẩn rồi từ đó phát sinh ra các đợt viêm nhiễm nặng hơn và có thể gây biến chứng cho trẻ. Vậy cách điều trị và phòng ngừa viêm VA ở trẻ như thế nào? Hãy cùng<b>ViCare</b>&nbsp;tìm hiểu thông tin chi tiết qua bài viết sau đây.</p></div></div><p><a href=\"https://vicare.vn/vicare-read/view/66/\" title=\"Ưu đãi tháng 12/2017 tại BV Mắt Quốc Tế Việt Nga\" class=\"vicaread\" style=\"background-color: rgb(255, 255, 255); color: rgb(0, 162, 162); transition: all 0.1s; display: block; margin: 10px 0px 20px; font-size: 14px;\"></a></p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">VA là gì?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">VA là một từ viết tắt hiện nay hay quen được sử dụng ở nước ta, nó có nguồn gốc từ 2 từ tiếng Pháp là “Végétations Adénoides”. Khối VA có từ khi trẻ mới lọt lòng, khi chưa bị viêm thì chúng có kích thước nhỏ, rất mỏng, xếp theo hình lá nên dễ tiếp xúc với bên ngoài và với kích thước này đường thở của trẻ hoàn toàn được đảm bảo bình thường.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Ở trẻ em hệ thống tế bào lympho quanh vùng họng mũi rất phát triển, tạo nên một vòng bảo vệ đường hô hấp khép kín gọi là vòng Waldeyer gồm các khối hạnh nhân gọi là amidan, khối lượng to nhất là amidan khẩu cái ta quen goi là Amidan, amidan đáy lưỡi, amidan ở loa vòi tai và cuối cùnglà khối tổ chức amidan ở trần vòm mũi họng hay còn được gọi là khối VA.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">VA phát triển nhanh theo lứa tuổi, tăng nhanh về khối lượng kể từ khi trẻ 2 tuổi, bắt đầu teo dần khi trẻ hơn 7 tuổi và biến mất gần như hoàn toàn khi trẻ vào độ tuổi dậy thì. VA còn sót lại ở người trưởng thành có tỷ lệ rất thấp.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Từ lúc mới sinh cho đến 6 tháng tuổi, trẻ sử dụng kháng thể của mẹ truyền qua nhau thai. Khi qua giai đoạn này, trẻ sẽ không còn được sự bảo vệ từ kháng thể của người mẹ cho như trước nữa. Vì vậy, trong quá trình thở, động tác hít vào thì không khí sẽ vào mũi, đi qua VA rồi vào khí quản và phổi, nên VA là nơi tiếp xúc với vi khuẩn nhiều nhất để tạo ra kháng thể tốt cho cơ thể. Đây là nhiệm vụ miễn dịch của VA để bảo vệ cơ thể cho trẻ từ 6 tháng tuổi trở lên. Từ 12 tháng tuổi, trẻ sẽ có thể bị<b>&nbsp;viêm VA</b>.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171212_033012_458140_319-VA.max-1800x1800.png\" alt=\"vicare.vn-cach-dieu-tri-va-phong-ngua-viem-va-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Nguyên nhân gây viêm VA ở trẻ</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Do bị nhiễm lạnh hoặc thói quen thường ăn đồ lạnh.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Các vi khuẩn, virus có sẵn ở mũi họng, khi có cơ hội chúng trở thành tác nhân gây bệnh hoặc do sự bội nhiễm của vi khuẩn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cấu trúc của VA có nhiều khe hốc, là nơi vi khuẩn dễ trú ẩn và phát triển. Hơn nữa VA nằm ở vòm mũi họng, là cửa ngõ ở đường thở nên vi khuẩn, virus dễ xâm nhập...<br></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cũng có thể trẻ bị viêm VA sau khi mắc một số bệnh nhiễm khuản hô hấp như: cúm, sởi, ho gà...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ô nhiễm môi trường sống (khói bụi, thuốc lá...) cũng là tác nhân khởi phát bệnh.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Trẻ bị giang mai bẩm sinh cũng là một yếu tố thuận lợi cho sự quá phát của viêm VA</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ở một số trẻ, do tổ chức bạch huyết phát triển rất mạnh, nhiều hạch ở cổ, ở họng quá phát rất dễ bị viêm nhiễm, tạo điều kiện thuận lợi cho bệnh viêm VA</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ngoài ra VA còn có rất nhiều nguy cơ khác liên quan đến yếu tố về nội tạng như cơ địa, thể trạng.<br></p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Triệu chứng viêm VA ở trẻ</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Sốt từ 38 – 39 độ C, cũng có thể sốt cao hơn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Trẻ bị chảy mũi, lúc đầu chảy mũi nước trong, loãng và sẽ chuyển biến chảy mũi nhầy, có mủ khi bệnh nặng hơn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Trẻ cũng bị ngạt mũi, dấu hiệu này thấy rõ hơn khi trẻ ngủ, ở những trẻ còn bú mẹ.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ho dai dẳng lâu ngày, và nếu có biến chứng của viêm phế quản thì ho sẽ trở nên trầm trọng hơn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Trẻ hay mệt mỏi, biếng ăn, quấy khóc và hơi thở hôi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"><b>Bệnh viêm VA</b>&nbsp;nặng sẽ dễ chuyển sang thành bệnh viêm phế quản và viêm tai giữa khi trẻ ho nhiều dữ dội, thở khò khè và mạnh hơn sau vài ngày sốt. Như vậy, các mẹ cần chú ý theo dõi tình trạng sức khỏe của trẻ để kịp thời điều trị cho bé sớm nhất.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Cách điều trị viêm VA ở trẻ</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thuốc hạ sốt: Khi trẻ sốt trên 38,5 độ, thuốc thường dùng nhất là các chế phẩm chứa paracetamol nhằm hạ sốt kịp thời cho trẻ tránh biến chứng nguy hiểm đối với cơ thể trẻ.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Các thuốc làm loãng đờm giảm ho như mucomyst, rhinathiol, xi-rô Ma Hạnh...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Các thuốc nhỏ mũi, thường dùng hai loại: Nước muối sinh lý hoặc muối biển, có tác dụng làm sạch mũi và argyrols 1% hoặc 2% có tác dụng sát khuẩn, làm khô.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Việc làm sạch mũi thường xuyên là rất quan trọng, ở trẻ lớn có thể làm sạch bằng cách thức đơn giản mà rất hiệu quả đó là xì mũi. Đối với trẻ nhỏ, chưa biết xì mũi, có thể phải dùng một quả bóng hút mũi mà hút cho trẻ, để có thể đẩy hết các chất nhầy ra ngoài và cho trẻ dễ thở hơn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Việc dùng kháng sinh trong đa số các trường hợp là không cần thiết, loại thuốc này chỉ được chỉ định trong những trường hợp trẻ bị VA nặng, có biến chứng hoặc đe dọa biến chứng và phải do thầy thuốc chỉ định, cha mẹ không nên tự mua kháng sinh cho con dùng.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Đối với các trường hợp đã có biến chứng như viêm phế quản, viêm tai giữa... nhất thiết phải đưa trẻ đến các thầy thuốc chuyên khoa có kinh nghiệm.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171212_033154_392893_tre-sot-cao-co-giat.max-1800x1800.jpg\" alt=\"vicare.vn-cach-dieu-tri-va-phong-ngua-viem-va-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Cách phòng bệnh viêm VA ở trẻ</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Giữ vệ sinh mũi họng cho trẻ hàng ngày, nên thường xuyên nhỏ mũi cho trẻ bằng dung dịch nước muối sinh lý có tác dụng vệ sinh mũi họng tốt.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Mùa đông cần cho trẻ mặc đủ ấm, đặc biệt cần giữ ấm cổ, giữ ấm bàn chân, không để trẻ đi chân trần, tránh nhiễm lạnh trong mùa đông.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cải thiện môi trường sống của trẻ, nhà ở thoáng đãng khô ráo về mùa hè, kín gió về mùa đông.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Điều trị sớm và đúng cách mỗi khi trẻ bị viêm mũi họng cấp hay<b>&nbsp;viêm VA</b>&nbsp;cấp.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Qua những thông tin&nbsp;<b>ViCare</b>&nbsp;cung cấp trên đây, các mẹ cần quan tâm và chăm sóc trẻ hàng ngày bằng cách vệ sinh răng miệng, giữ ấm cho trẻ vào mùa đông để tránh những bệnh liên quan đến tai mũi họng, tránh những trường hợp gây bệnh nguy hiểm cho bé làm ảnh hưởng đến sự phát triển thể chất và trí não của bé.</p>', 37, NULL, '2018-01-10 14:58:48'),
(3, 8, 1, 'Chủ đề khác Bệnh nhân thấp khớp kiêng ăn gì?', 'post_1235.jpg', '<div style=\"color: rgb(51, 51, 51); font-size: 14px;\"><div class=\"rich-text\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Theo các nghiên cứu khoa học thì đối với bệnh thấp khớp, bên cạnh dùng thuốc điều trị thì việc điều chỉnh chế độ ăn uống hàng ngày sẽ góp phần đạt được kết quả điều trị tốt nhất. Sau đây,&nbsp;<b>ViCare</b>&nbsp;sẽ mách bạn một số loại thực phẩm mà người bị&nbsp;<b>bệnh thấp khớp</b>&nbsp;nên tránh để không làm trầm trọng thêm tình trạng bệnh lý.</p></div></div><a href=\"https://vicare.vn/vicare-read/view/59/\" title=\"Miễn Phí Phát Hiện Sớm Ung Thư - Kết Quả Của BV Bệnh Nhiệt Đới TW\" class=\"vicaread\" style=\"background-color: rgb(255, 255, 255); color: rgb(0, 162, 162); transition: all 0.1s; display: block; margin: 10px 0px 20px; font-size: 14px;\"></a><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">1. Thấp khớp là bệnh gì?</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"><b>Thấp khớp</b>&nbsp;là tình trạng sức khỏe ảnh hưởng đến cơ bắp, xương, khớp và đôi khi được coi là bệnh viêm thấp khớp.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Phần lớn, người ta dùng từ “thấp khớp” để đề cập đến bệnh viêm khớp dạng thấp. Một số quốc gia dùng từ “thấp khớp” để miêu tả hội chứng đau cơ xơ hóa. Thấp khớp có 2 dạng:</p><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Thấp khớp liên quan tới khớp: những tình trạng ảnh hưởng đến khớp xương bao gồm viêm khớp dạng thấp, lupus, Gút, viêm đốt sống, v.v.;</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Thấp khớp không liên quan đến khớp: tình trạng sức khỏe ảnh hưởng đến các phần mô mềm và cơ như viêm khớp dạng thấp.</p></li></ul><figure class=\"post-img-cover full\" style=\"text-align: justify; margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171211_162657_953206_2927-viem-khop-dang.max-1800x1800.jpg\" alt=\"vicare.vn-benh-nhan-thap-khop-kieng-an-gi-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">2. Những dấu hiệu và triệu chứng của bệnh thấp khớp&nbsp;<i></i></h2><ul style=\"margin-right: 0px; margin-left: 20px; padding-left: 20px; color: rgb(51, 51, 51); font-size: 14px;\"><li style=\"margin: 0px 0px 10px;\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Khớp bị cứng, thường tệ hơn vào buổi sáng và sau khi không hoạt động. Tình trạng này có thể kéo dài 1-2 giờ (hoặc thậm chí cả ngày);</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Khớp yếu, ấm lên và sưng;</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Biến dạng khớp. Khi sụn và sụn nang khớp bị tổn thương nghiêm trọng, toàn bộ khớp có thể trở nên biến dạng. Tình trạng này thường là do không phát hiện và điều trị kịp thời các bệnh mãn tính;</p></li><li style=\"margin: 0px 0px 10px;\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Mệt mỏi, sốt và sụt cân.</p></li></ul><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Ban đầu, bệnh thấp khớp thường ảnh hưởng đến các khớp nhỏ, đặc biệt là các khớp nối ngón tay với bàn tay và các ngón chân với bàn chân. Khi bệnh tiến triển, các triệu chứng thường lây lan đến khớp cổ tay, đầu gối, mắt cá chân, khuỷu tay, hông và vai. Trong hầu hết các trường hợp, các triệu chứng xảy ra với các khớp hai bên cơ thể.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Bệnh<b>&nbsp;thấp khớp</b>&nbsp;có thể ảnh hưởng đến các bộ phận cơ thể khác như: da, mắt, phổi, tim, thận, tuyến nước bọt, mô thần kinh, tủy xương...</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">3. Những nguyên nhân nào gây ra bệnh thấp khớp?</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Tuổi già hoặc các tình trạng sức khỏe yếu, đặc biệt là nhiễm trùng cấu trúc cơ xương có thể gây ra thấp khớp và dẫn đến viêm dày màng hoạt dịch, gây phá hủy sụn và xương trong khớp. Bệnh cũng làm yếu và kéo căng gân cũng như dây chằng nối các khớp, làm cho các khớp biến dạng dần dần.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Hiện các, bác sĩ vẫn chưa xác định rõ nguyên nhân gây ra tình trạng này, mặc dù bệnh có thể liên quan đến các yếu tố gen. Gen không thực sự gây ra thấp khớp, nhưng có thể khiến bạn dễ bị tổn thương hơn bởi các yếu tố môi trường như nhiễm một số loại virus và vi khuẩn gây bệnh.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Căn bệnh thấp khớp là loại bệnh ngày càng phổ biến và có tính miễn dịch tự thân. Không chỉ người già mới có nguy cơ mắc<b>&nbsp;thấp khớp</b>&nbsp;mà bệnh lý này cũng đang có diễn tiến ngày càng trẻ hóa. Việc điều trị chủ yếu hiện nay vẫn sử dụng các thuốc kháng viêm không steroid và gluco corticoide để giúp giảm đau, tiêu viêm. Tuy nhiên, hiện nay việc điều trị cũng gặp rất nhiều khó khăn vì thuốc điều trị thường có nhiều tác dụng phụ, giá thành cao và có thể ảnh hưởng đến sinh hoạt của bệnh nhân hàng ngày, vì vậy một chế độ ăn uống hợp lý trong điều trị bệnh thấp khớp có ý nghĩa quan trọng.</p><figure class=\"post-img-cover full\" style=\"text-align: justify; margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171211_162748_427430_bui-det-may-co-the-.max-1800x1800.jpg\" alt=\"vicare.vn-benh-nhan-thap-khop-kieng-an-gi-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">4. Bị thấp khớp cần kiêng ăn gì?</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Người bị bệnh thấp khớp nên kiêng ăn phủ tạng động vật, hạn chế thực phẩm chứa đạm động vật, thịt đỏ. Thay vì đạm động vật, người bệnh nên sử dụng đạm thực vật để tốt cho sức khỏe. Cần tăng cường uống sữa vì đây là nguồn cung cấp canxi quan trọng, rất tốt cho người bệnh khớp.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Không nên ăn thực phẩm quá mặn (lượng muối không quá 10g/ngày), hạn chế đồ uống ngọt vì chúng chứa nhiều đường và hàm lượng photpho cao.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Những thực phẩm gây tăng chất lipit máu, gây bất lợi cho người đang bị viêm khớp như thịt mỡ, bơ, xúc xích, dăm bông, bánh kẹo cũng nên được cắt giảm tối đa trong khẩu phần ăn hàng ngày. Tránh các đồ ăn nhanh, thức ăn sẵn, thức ăn chiên quá kỹ và nhiều dầu mỡ.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Các thực phẩm dễ gây dị ứng và các sản phẩm làm tình trạng viêm đau trở nên tồi tệ hơn như ngô, sản phẩm bơ sữa đã qua chế biến, quả thuộc họ cam quýt nên hạn chế tối đa. Ngô có nhóm hoạt chất dễ gây nên tình trạng dị ứng dưới dạng viêm khớp. Những thực phẩm giàu axit oxalic như việt quất, mận, củ cải cũng nên hạn chế.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bệnh nhân thấp khớp cũng cần phải kiêng ăn những loại thực phẩm có tính nóng, nhiệt như: gừng, tỏi, ớt...</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Người bị bệnh&nbsp;<b>thấp khớp&nbsp;</b>trong quá trình hỗ trợ điều trị cần phải cắt giảm những món chứa nhiều tinh bột bởi bệnh nhân ăn nhiều tinh bột như bột mì sẽ khiến tình trạng viêm khớp tăng nặng hơn.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bệnh nhân không nên sử dụng các chất kích thích, đặc biệt là không nên uống cà phê, soda, các loại rượu bia. Trong cà phê có chứa caffein là chất khiến triệu chứng viêm thêm tồi tệ hơn. Riêng soda sẽ làm cho những cơn đau nhức do thấp khớp tăng nặng thêm.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">5. Bệnh nhân bị thấp khớp nên ăn gì?</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đối với những người béo phì cần giảm cân, cân bằng chế độ ăn kiêng để làm giảm tối đa những chấn động và áp lực trên khớp. Người có tình trạng dinh dưỡng kém mà bị thấp khớp thì nên ăn nhiều hơn bình thường bằng cách ăn thành nhiều bữa nhỏ để cơ thể dung nạp tốt hơn.</p><span style=\"color: rgb(51, 51, 51); font-size: 14px;\"><div style=\"text-align: justify;\">- Bệnh nhân bị bệnh thấp khớp thì thức ăn càng đa dạng càng tốt. Ăn nhiều rau củ quả, thực phẩm giàu chất xơ, đặc biệt là đối với những người thường xuyên bị táo bón.</div></span><figure class=\"post-img-cover full\" style=\"text-align: justify; margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171211_162825_412013_521209120-1.max-1800x1800.jpg\" alt=\"vicare.vn-benh-nhan-thap-khop-kieng-an-gi-body-3\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Các loại thực phẩm giàu chất đạm như thịt, cá, trứng, sữa, các loại hải sản, tào phớ và đậu các loại... đều có lợi cho sức khỏe. Bữa ăn nên sử dụng dầu cá thay mỡ bởi nó có tác dụng giảm đau và giảm sưng khớp.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bổ sung thêm vitamin D, B, K, folic acid, calcium, sắt có chứa trong các loại rau.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Dùng các loại dầu chứa acid béo omega 3 như: dầu đậu nành, dầu hạnh nhân, dầu ô-liu...</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Trên đây là một số thực phẩm mà người bệnh&nbsp;<b>thấp khớp</b>&nbsp;nên kiêng. Khi mắc căn bệnh này đồng nghĩa với việc bạn phải kiêng ăn các loại thực phẩm trên suốt đời nếu như không muốn tình trạng bệnh của mình tái phát hoặc tiến triển nặng hơn.</p>', 29, NULL, '2018-01-03 03:21:11'),
(9, 8, 1, 'Cách làm mờ vùng thâm quanh miệng', 'post_GgPO.jpg', '<div style=\"color: rgb(51, 51, 51); font-size: 14px;\"><div class=\"rich-text\"><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 0px;\">Trên khuôn mặt của chị em phụ nữ, vùng da tối màu nhất ngoài phần quầng mắt khiến chúng ta thiếu tự tin là viền môi và khoé miệng. Trị&nbsp;<b>vết thâm quanh miệng</b>, thâm môi luôn là nhu cầu cần thiết. Bởi có đôi môi đẹp, cái miệng xinh thì bạn sẽ trở nên duyên dáng hơn.&nbsp;<b>ViCare&nbsp;</b>sẽ gợi ý cho các bạn những cách làm mờ vùng<b>&nbsp;thâm quanh miệng</b>&nbsp;hiệu quả.</p></div></div><p><a href=\"https://vicare.vn/vicare-read/view/66/\" title=\"Ưu đãi tháng 12/2017 tại BV Mắt Quốc Tế Việt Nga\" class=\"vicaread\" style=\"background-color: rgb(255, 255, 255); color: rgb(0, 162, 162); transition: all 0.1s; display: block; margin: 10px 0px 20px; font-size: 14px;\"></a></p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Môi, miệng là nơi dễ bị tổn thương. Do da vùng này rất mỏng và dễ bị dị ứng nên có thể gây tổn thương tới da. Hậu quả của nó là để lại những nốt thâm đen xung quanh môi nhìn rất khó chịu. Sự thay đổi màu da xung quanh miệng, trán, mũi hay môi không phải là một dấu hiệu bất thường.Đó là kết quả của việc tăng sắc tố da có thế xảy ra với một số lý do đó là: Chất melanin có xu hướng làm tăng sắc tố da tập trung ở vùng quanh miệng khi tiếp xúc với ánh sáng mặt trời. Bên cạnh đó, lớp da xung quanh miệng rất mỏng, dễ khiến cho da bị đổi màu, da khô và có nếp nhăn. Thay đổi hócmôn và cách sinh hoạt cũng là nguyên nhân gây ra những dấu hiệu này.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Dưới đây là một số cách giúp bạn dễ dàng thực hiện ngay tại nhà và có hiệu quả rõ rệt trong việc làm mờ vết thâm vùng da quanh miệng:</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">1. Hạn chế tiếp xúc ánh nắng trực tiếp</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Để làm mờ vùng<b>&nbsp;thâm quanh miệng</b>, bạn cần phải sử dụng kem chống nắng thường xuyên. Loại kem có chỉ số SPF30 sẽ bảo vệ da bạn khỏi các tia cực tím từ mặt trời. Kem chống nắng chứa kẽm, titan và avobenzone là thành phần cực kỳ hiệu quả để ngăn chặn tia UVA và UVB.</p><figure class=\"post-img-cover full\" style=\"text-align: center; margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171212_044822_550878_chngnnghiuqu.max-1800x1800.jpg\" alt=\"vicare.vn-cach-lam-mo-vung-tham-quanh-mieng-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"text-align: center; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">2. Dùng nước chanh</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nước chanh có tác dụng giảm mờ vết thâm. Nước chanh là một chất tẩy trắng tự nhiên tốt nhất để khắc phục và giảm khô da xung quanh miệng. Bạn chỉ cần vắt lấy nước cốt chanh, bỏ hết hạt rồi xoa đều lên vùng da quanh miệng rồi để 15, 20 phút.</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">3. Sử dụng dầu dừa và nước cốt dừa</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Chà dầu dừa vào buổi tối trước khi ngủ để giảm vùng thâm.Nước dừa thì có tác dụng làm mềm da. Cách đơn giản là ban rửa mặt bằng nước dừa 2 lần/ngày và cảm nhận tác dụng của chúng.</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">4. Hỗn hợp đậu xanh, bột nghệ và sữa</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nghệ có rất nhiều tác dụng tốt cho da như tẩy tế bào chết, trị mụn, làm mờ sẹo, thu nhỏ lỗ chân lông, hạn chế nhờn và chống lại các tác hại của ánh nắng lên da. Chăm sóc da bằng mặt nạ bột nghệ hay nghệ tươi thì chị em nào cũng phải công nhận làn da sẽ đẹp hơn, trắng hơn và mịn màng hơn.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cách làm: Trộn đều 1 thìa bột nghệ, 1 thìa bột đậu xanh, 2 - 3 thìa sữa tươi để tạo thành dung dịch đặc sệt sau đó thoa lên vùng da quang miệng và để trong vòng 20p sau đó rửa lại với nước sạch</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">5. Hỗn hợp chanh và cà chua</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cà chua có tác dụng làm mịn da, trắng da, xe khít lỗ chân lông, loại bỏ các nếp nhăn xấu xí và đánh bay mụn. Kết hợp giữa chanh và cà chua là sự kết hợp hoàn hảo để làm giảm độ thâm của vùng da quanh miệng.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cách làm: Nghiền 1 quả cà chua và sử dụng 3-4 giọt chanh rồi trộn đều và đắp hỗn hợp lên da khoảng 20 phút. 1 tuần bạn chỉ cần làm 2 lần sẽ thấy chuyển biến rõ nét.</p><figure class=\"post-img-cover full\" style=\"text-align: justify; margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171212_044856_455229_trang-da-3-blogtams.max-1800x1800.jpg\" alt=\"vicare.vn-cach-lam-mo-vung-tham-quanh-mieng-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">6. Hỗn hợp bột yến mạch, sữa chua và nước ép cà chua</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Xay nhuyễn 1 thìa bột yến mạch sau đó trộn với 1/4 quả chanh và nước ép 1/2 quả cà chua. Hỗn hợp bột yến mạch,sữa chua và nước ép cà chua làm da sáng đều và có thể giảm nếp nhăn trên môi.</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">7. Nước ép khoai tây</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Rửa sạch củ khoai tây tươi ép lấy nước sau đó thoa nước ép khoai tây trong vòng 15p để khắc phục tình trạng này.</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">8. Glycerin</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Glycerin là một loại hoá chất dạng keo có đặc tính dưỡng ẩm và dưỡng trắng tuyệt vời đã từng được sử dụng nhiều trong công nghệ làm đẹp hiện đại và đặc biệt an toàn cho da. Việc sử dụng glycerin cho vùng da quanh miệng thật sự rất nhanh chóng và đem lại hiệu quả cao.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cách làm vô cùng đơn giản, bạn chỉ cần nhúng một cây tăm bông vào lọ glycerin nguyên chất sau đó xoa đều vùng ra khoé miệng và viền môi và giữ chúng qua đêm. Điều này giúp nuôi dưỡng sâu làn da khô ráp bong tróc thường xảy ra vào mùa đông và cải thiện dần dần màu da tối và khiến chúng mềm mại hơn, dễ dàng hơn trong việc sử dụng kem che khuyết điểm để che đi nếu cần thiết.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Tuy nhiên Glycerin rất dính vì vậy không bôi quá nhiều tránh cảm giác khó chịu và bất tiện khi ngủ.</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">9. Hỗn hợp chuối + mật ong + sữa chua</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Hợp chất từ chuối có thể dùng cho mọi loại da, mọi độ tuổi, vì chúng làm da mềm mại và không gây kích thích phản ứng và đặc biệt hữu ích đối với da khô. Ngoài ra, thành phần mật ong ẩn chứa một công dụng tuyệt vời giúp da bạn nữ phục hồi và trẻ trung. Trong mật ong tự nhiên có chứa chất chống ô-xi hóa, chất kháng khuẩn và chống nấm mốc giúp bảo vệ da bạn khỏi những tác hại của môi trường xung quanh từ đó giúp da trông khỏe khoắn, mềm mịn và rạng ngời tự nhiên.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cách làm: dầm nhuyễn 1/2 quả chuối, 1 thìa mật ong, 2- 3 thìa sữa chua và trộn đều các nguyên liệu, hoặc đánh mịn bằng máy xay. Thoa đều lên vùng da quanh miệng và giữ trên da khoảng 20 phút cho đến khi hỗn hợp đặc và cứng lại trên da bạn. Rửa sạch bằng nước ấm. Hỗn hợp này không những là mặt nạ cho vùng da không đều màu, mà còn là một loại sinh tố rất ngon và tốt cho sức khoẻ nữa đó. Sử dụng thường xuyên để đạt hiệu quả cao.</p><figure class=\"post-img-cover full\" style=\"text-align: justify; margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171212_044952_937697_1428122694-hbwrmat_.max-1800x1800.jpg\" alt=\"vicare.vn-cach-lam-mo-vung-tham-quanh-mieng-body-3\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">10. Hạnh nhân</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đối với làn da, hạnh nhân có tác dụng cải thiện cấu trúc bề mặt da và làm chậm quá trình lão hóa. Ngoài cách hấp thu hạnh nhân bằng cách ăn trực tiếp, phụ nữ chúng ta còn có thể dùng hạnh nhân dưới dạng tinh dầu và bột hạnh nhân để chăm sóc làn da của mình.</p><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Bạn chỉ cần trộn 1 thìa bột hạnh nhân, 1 thìa bột sữa, 3- 4 thìa nước sạch để có hỗn hợp bột sệt. Bạn có thể dùng sữa tươi thay cho sữa bột. Thoa hỗn hợp lên vùng da cần cải thiện, massage nhẹ nhàng trong khoảng 5 phút rồi rửa sạch với nước lạnh. Công thức sữa rửa mặt này có tác dụng tẩy tế bào chết cho da, lấy đi bụi bẩn và cải thiện bề mặt da.</p><h2 style=\"text-align: justify; font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">11. Che khuyết điểm</h2><p style=\"text-align: justify; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Kem che khuyết điểm mặc dù không có tác dụng cải thiện dần đều vùng da tối màu quanh miệng, tuy nhiên đây lại là giải pháp nhanh gọn nhất để tôn tạo làn da đều màu và che đi những vùng da tối và vết thâm. Mẹo nhỏ để đôi môi mịn màng đều màu đó là sử dụng kem che khuyết điểm dạng lỏng, chấm những chấm nhỏ xung quang miệng và viền môi, dùng tay tán nhẹ. Vào những ngày trời hanh khô, đừng quên sử dụng kem lót, hoặc kem dưỡng cho vùng da này trước khi che khuyết điểm.</p>', 17, NULL, '2018-01-10 14:59:04');
INSERT INTO `post` (`id`, `userId`, `categoryId`, `name`, `avatar`, `content`, `views`, `unsigned_name`, `createdAt`) VALUES
(10, 9, 2, 'Trẻ bị viêm Amidan sốt bao lâu?', 'post_IorB.jpg', '<p><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">Nhiều căn bệnh thường gặp ở trẻ em có triệu chứng sốt, trong đó có bệnh&nbsp;</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">viêm amidan</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">. Khi trẻ mắc bệnh thường bị sốt, ngoài việc quan tâm điều trị triệu chứng sốt, các mẹ còn phải chú ý xác định chính xác căn bệnh bé đang mắc phải để xử trí cho đúng đắn. Thời gian lành bệnh cũng tùy thuộc rất lớn vào cách xử trí của cha mẹ có phù hợp với bản chất bệnh lý hay không.</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Trước hết, các mẹ cần xác định xem bé bị sốt có phải vì bị viêm amidan hay không bằng cách quan sát những triệu chứng trên cơ thể bé.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\"><span style=\"font-weight: 900;\">Các biểu hiện của&nbsp;</span><i style=\"\"><a href=\"https://vicare.vn/benh/viem-amidan-1666/\" style=\"color: rgb(0, 162, 162); transition: all 0.1s;\"><b style=\"\">bệnh viêm amidan</b></a></i></h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Viêm amidan có các loại<b>&nbsp;viêm amidan cấp tính</b>&nbsp;và&nbsp;<b>viêm amidan mãn tính</b>.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Viêm amidan cấp tính</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Viêm amidan cấp thường do virut hoặc vi khuẩn gây nên, là xuất tiết hoặc viêm mủ của tuyến amidan khẩu cái và tổn thương viêm sung huyết. Nếu do vi khuẩn thì bệnh nặng; trái lại nếu do virut gây bệnh thì thường là nhẹ . Do liên cầu tan huyết β nhóm A là trường hợp nặng nhất.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Triệu chứng gồm: khởi bệnh đột ngột với cảm giác nhức đầu, người mệt mỏi, chán ăn, rét và gai rét rồi sốt 38 - 39oC; hội chứng nhiễm khuẩn, nước tiểu đỏ. Bệnh nhân nuốt vướng, nuốt đau; họng có cảm giác nóng và khô rát ở trong; đau họng, đau nhói lên tai, đau tăng lên khi ho và khi nuốt. Khò khè khó thở, thở, ngáy to. Khi viêm nhiễm lan xuống đường hô hấp dưới, phế quản bị viêm sẽ gây khàn tiếng, đau tức ngực, và ho từng cơn có đờm nhầy.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_103059_629620_dieu-tri-viem-amida.max-1800x1800.jpg\" alt=\"vicare.vn-tre-bi-viem-amidan-sot-bao-lau-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Khám thấy lưỡi trắng bẩn, môi khô. Nếu các tổ chức bạch huyết thành sau họng cũng sưng to và đỏ, amidan sưng to và đỏ, toàn bộ niêm mạc họng đỏ rực và xuất tiết trong, thì kết luận do virut gây bệnh.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nếu trên bề mặt có những mảng bựa trắng hoặc chấm mủ trắng, amidan sưng to và đỏ thì kết luận do vi khuẩn gây bệnh. Tuy nhiên, việc phân biệt viêm amidan do vi khuẩn và virut như trên chỉ có tính chất tương đối.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Viêm amidan mạn tính</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Tình trạng viêm đi viêm lại tuyến amidan dẫn đến&nbsp;<b>viêm amidan&nbsp;</b>mạn tính. Bệnh nhân nuốt vướng, cảm giác ngứa vướng và rát trong họng, thỉnh thoảng phải khạc nhổ do xuất tiết, hay sốt vặt; về buổi sáng khi mới ngủ dậy hơi thở hôi do chất mủ chứa trong các hốc của amidan và thường ho khan từng cơn.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Do đâu bị viêm amidan?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nguyên nhân đầu tiên phải kể đến là những thay đổi tiêu cực từ môi trường sống: thời tiết giao mùa, thay đổi đột ngột, khói bụi trong không khí (đặc biệt là khói thuốc lá, khói xe, hơi than...), trẻ mới đi mẫu giáo hay mới cai sữa hoặc thay đổi chế độ ăn dặm...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Những thay đổi tiêu cực từ môi trường khiến hệ miễn dịch chưa hoàn thiện của các bé trở nên yếu đi trước sự tấn công ồ ạt của các virut, nấm và vi khuẩn đang phát triển rất mạnh trong môi trường nhiệt độ thấp mà độ ẩm lại cao.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Một số trẻ có nhiều hạch ở cổ, ở họng quá phát, tổ chức bạch huyết phát triển rất mạnh, rất dễ bị viêm nhiễm, tạo điều kiện thuận lợi gây ra&nbsp;<b>viêm amidan</b>.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nguyên nhân cũng có thể do cấu trúc của amidan: có nhiều khe hốc, mặt khác, amidan nằm ở giao điểm của đường thở và đường ăn, là cửa ngõ cho virut, vi khuẩn xâm nhập gây bệnh.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Sốt do viêm amidan có cần làm xét nghiệm máu?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Thực tế, không nên xét nghiệm máu tất cả bệnh nhi bị sốt để tìm bệnh, chỉ nên thực hiện xét nghiệm máu cho những bệnh nhi bị sốt từ ngày thứ 3 trở đi vì nếu thực hiện sớm hơn cũng không phát hiện hay loại trừ được bệnh. Những bệnh nhi sốt ngày 1, ngày 2 khi cần cũng phải xét nghiệm lại vào ngày thứ 3 của bệnh. Như vậy, những bệnh nhi này phải chịu hai lần xét nghiệm mà lần đầu là không cần thiết. Tuy nhiên, bác sĩ có thể cho thử máu sớm hơn vào ngày thứ 1, thứ 2 nhưng với mục đích chẩn đoán nguyên nhân gây sốt có liên quan đến sốt rét hay các bệnh nhiễm trùng .</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_103155_919802_8A-anh-tien-bo-y-ho.max-1800x1800.jpg\" alt=\"vicare.vn-tre-bi-viem-amidan-sot-bao-lau-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Sốt do viêm amidan bao lâu sẽ hết?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Hầu hết các cơn sốt do virus nói chung và sốt do&nbsp;<b>viêm amidan</b>&nbsp;nói riêng sẽ kéo dài khoảng 3 đến 5 ngày. Thời gian lành bệnh cũng còn tùy thuộc rất lớn vào cách xử trí, chăm sóc của cha mẹ có phù hợp với bản chất bệnh lý hay không. Sốt không gây tác hại kéo dài, mà những biến chứng của căn bệnh bé đang mắc mới đáng lo ngại.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Viêm amidan và các biến chứng</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"><b>Viêm amidan</b>&nbsp;có thể gây ra nhiều biến chứng: viêm thanh khí phế quản, viêm tấy hạch dưới hàm, áp-xe amidan, áp-xe thành bên họng, viêm tấy quanh amidan, nhiễm khuẩn huyết , viêm khớp...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Vì là một bệnh có thể gây ra nhiều di chứng và biến chứng nguy hiểm, cần phải được phát hiện và điều trị sớm. Mỗi đợt dùng thuốc khoảng 10 ngày do các bác sĩ tai mũi họng theo dõi và điều trị. Kể cả khi trẻ đã cắt sốt, các bậc phụ huynh vẫn nên tuân thủ phác đồ điều trị của bác sĩ để chữa dứt điểm bệnh, tránh tái phát liên miên và những hệ quả kèm theo.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Phương pháp điều trị, chăm sóc trẻ bị viêm amidan thế nào là đúng?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đối với viêm amidan cấp: tốt nhất theo kháng sinh đồ. Điều trị triệu chứng, dùng các thuốc giảm đau, kháng viêm, hạ sốt, giảm ho, thuốc làm lỏng chất nhầy. Pha nước muối loãng hoặc dùng nước muối sinh lý NaCl 0,9% súc họng, nhỏ mũi (Khuyến khích dùng nước muối sinh lý để đảm bảo vệ sinh và tỉ lệ muối – nước được cân bằng). Bệnh nhân cần được ăn thức ăn dễ tiêu, giàu dinh dưỡng, nằm nghỉ, uống nước đầy đủ.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đối với viêm amidan mãn tính: phẫu thuật cắt amidan. Cắt amidan là phương pháp điều trị hữu hiệu nhằm loại bỏ tổ chức amidan trở thành một ổ viêm nghi ngờ phát triển thành u ác tính và không còn vai trò miễn dịch hoặc bít tắc hô hấp trên,.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Những trường hợp cần cắt amidan:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">-&nbsp;<b>Viêm amidan</b>&nbsp;mãn tính có hơn 6 đợt tái phát liên tiếp hoặc kéo dài trên 6 tuần</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Áp xe quanh amidan phải nhập viện điều trị.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Viêm amidan gây biến chứng viêm xoang, viêm vi cầu thận, sốt thấp khớp, hoặc gây viêm tai giữa, ... tái đi tái lại nhiều lần.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Nghi ngờ ung thư amiđan do amiđan chỉ to một bên kèm sưng hạch cổ cùng bên.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thường cắt amidan sau 4 tuổi.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_103234_042382_mo-amidan-duoc-thuc.max-1800x1800.jpg\" alt=\"vicare.vn-tre-bi-viem-amidan-sot-bao-lau-body-3\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Phòng ngừa viêm amidan</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"><b>Viêm amidan&nbsp;</b>là một bệnh thường gặp, gây các biến chứng nguy hiểm, vì vậy việc phòng bệnh rất quan trọng. Các biện pháp phòng bệnh hiệu quả gồm: khi cơ thể đang yếu hoặc trời lạnh không ăn kem, uống nước đá vì dễ mắc bệnh; tránh bị lạnh bằng việc mặc ấm, quàng khăn. Giữ vệ sinh mũi và răng miệng bằng việc súc miệng đánh răng hàng ngày. Đeo khẩu trang khi ra đường hay khi đến nơi đông người. Nâng cao sức đề kháng của cơ thể bằng việc dinh dưỡng hợp lý, rèn luyện thân thể.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Khi nào cần cho trẻ đi khám?</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nên cho trẻ đi khám ngay lập tức nếu:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Con có biểu hiện rất mệt mỏi (sốt thường đi cùng với chóng mặt, nhức đầu, cứng gáy, nổi ban, thở khó khăn).</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Sốt cao trên 40 độ C sau 2h uống thuốc hạ sốt.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Con dưới 3 tháng tuổi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cho con đi khám trong vòng 24 giờ nếu:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Con đã hết sốt hơn 24 tiếng nhưng sau đó lại bị sốt lại và sốt cao hơn 39 độ.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Con từ 3 đến 6 tháng tuổi (trừ khi sốt do mũi chích ngừa)</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Con bị sốt kéo dài hơn 3 ngày.</p><p><br></p>', 1, NULL, '2017-12-14 13:41:18'),
(11, 9, 2, 'Cách phòng tránh bệnh viêm mũi dị ứng', 'post_BCtO.jpg', '<p><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">Viêm mũi dị ứng</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;tuy không nguy hiểm như một số căn bệnh khác song lại thường xuyên tái phát, gây ảnh hưởng nghiêm trọng tới sinh hoạt thường ngày của người bệnh. Trong bài viết dưới đây,&nbsp;</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">ViCare</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;xin gửi tới bạn đọc những cách phòng tránh bệnh viêm mũi dị ứng trong thời tiết mùa đông khắc nghiệt này.</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"><b>Viêm mũi dị ứng</b>&nbsp;tuy không nguy hiểm với tính mạng song lại ảnh hưởng lớn tới chất lượng cuộc sống của bệnh nhân. Nghiêm trọng hơn, đây cũng là một trong những nguyên nhân chính dẫn tới bệnh viêm xoang mãn tính nên mọi người cần biết được những triệu chứng của bệnh, dị nguyên gây bệnh để từ đó học cách phòng tránh bệnh viêm mũi dị ứng.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Biểu hiện của bệnh viêm mũi dị ứng</h2><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Hắt hơi</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đây là triệu chứng điển hình của bệnh viêm mũi dị ứng. Các cơn hắt hơi mang tính đột ngột, xảy ra liên tục và nhiều lần, kéo dài nhiều phút và thường tái phát trong đợt dị ứng.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Ngứa mũi</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Triệu chứng này thường xuất hiện từ sớm, nhất là ở trẻ nhỏ. Đôi khi, bệnh nhân sẽ thấy ngứa cả họng, mắt, mũi hoặc vùng da ống tai ngoài, cổ.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_091451_637312_14_11_2016_16_35_04.max-1800x1800.jpg\" alt=\"vicare.vn-cach-phong-tranh-benh-viem-mui-di-ung-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Chảy nước mũi</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Thường đi kèm với hắt hơi hoặc xảy ra sau hắt hơi. Người bệnh viêm mũi dị ứng bị chảy nước mũi ở cả 2 bên, nước trong suốt, không có mùi.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Tắc ngạt mũi</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Người bị&nbsp;<b>viêm mũi dị ứng</b>&nbsp;có thể bị tắc ngạt mũi hoặc thậm chí là ngạt hoàn toàn cả 2 bên mũi do sự phù nề của niêm mạc và hiện tượng chảy nhiều nước mũi làm ngạt mũi. Người bệnh phải thở bằng miệng, đôi khi gây cảm giác ngạt thở ở trẻ nhỏ.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Đau</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Do thiếu thở nên bệnh nhân có thể cảm thấy uể oải, mệt mỏi, nhức đầu, khả năng lao động tay chân và trí não giảm bên cạnh cảm giác đầy trong mũi hoặc ngạt cứng trong mũi. Trong một số trường hợp, người bệnh đau ở vùng mũi, vùng xoang mặt và có thể kèm theo rối loạn vận mạch vùng mặt.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Dị ứng</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Dị ứng mũi thường đi kèm dị ứng cả ổ mặt, vùng mặt nên màng tiếp hợp bị đỏ, ngứa mắt, chảy nhiều nước mắt.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Các dị nguyên gây bệnh viêm mũi dị ứng</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Trước khi tìm hiểu cách phòng tránh bệnh<b>&nbsp;viêm mũi dị ứng</b>, cần nắm được những loại dị nguyên gây bệnh để hạn chế chính các dị nguyên này. Các dị nguyên gây bệnh viêm mũi dị ứng thường do những vật thể lạ bay lẫn trong khí quyển như:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Lông động vật: Chó, mèo, ngựa,...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bụi khói công nghiệp.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Nấm mốc.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bụi nhà, trong đó tác nhân chính là con mạt (acarien).</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Phấn hoa.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Các loại hoá chất.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Ngoài ra, những chất lạ trong thực phẩm như hải sản, các loại đậu, trứng, sữa, thuốc kháng sinh, chất bôi trơn ở xà phòng, bao cao su,... đều có thể gây&nbsp;<b>viêm mũi dị ứng</b>. Những chất này thường gây triệu chứng ở bộ máy tiêu hoá hoặc triệu chứng toàn thân nhưng cũng cho biểu hiện ở đường thở trên.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Một người có bị dị ứng hay không còn phụ thuộc và cơ địa và tố chất di truyền. Tiền sử gia đình đóng vai trò rất quan trọng, nếu cha mẹ bị dị ứng sẽ có tỷ lệ con bị dị ứng cao; trong đó nếu chỉ cha hoặc mẹ bị dị ứng thì tỷ lệ con bị dị ứng là 30% và nếu cả cha và mẹ bị dị ứng thì tỷ lệ này là 50%.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">(Theo số liệu từ báo VnExpress)</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_091543_546735_nhan-biet-khi-bi-di.max-1800x1800.jpg\" alt=\"vicare.vn-cach-phong-tranh-benh-viem-mui-di-ung-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Cách phòng tránh bệnh viêm mũi dị ứng</h2><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Tránh tiếp xúc với dị nguyên gây bệnh</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cách điều trị cũng như phòng tránh viêm mũi dị ứng tốt nhất là tránh tiếp xúc với những dị nguyên gây ngứa và phòng ngừa không để các triệu chứng của bệnh xảy ra.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Giữa không gian sống thoáng khí, khô sạch, thường xuyên hút bụi, không nuôi chó mèo và tích cực diệt gián, chuột.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cần loại bỏ con mạt và nấm mốc, nhất là ở những nơi thiếu ánh sáng, sách báo cũ, giày cũ, các loại hoa khô, thảm trải nền nhà, chăn, chiếu, giấy dán tường, cây cảnh,...</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Tăng cường sức đề kháng</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Giữ ấm: Đây là một trong những cách phòng tránh bệnh&nbsp;<b>viêm mũi dị ứng&nbsp;</b>đơn giản và hiệu quả nhất. Tất cả những gì bạn cần làm chỉ là giữ ấm cơ thể, nhất là vùng mũi, ngực,cổ và gan bàn chân trong mùa đông, không tắm nước lạnh. Với những người thường xuyên dậy sớm và phải thức quá khuya, cần chú tâm hơn tới việc giữ ấm bởi đây là thời điểm dễ bị cảm và dễ chuyển thành bệnh viêm mũi xoang. Tránh hít luồng không khí khô lạnh một cách đột ngột hoặc để mũi trực tiếp tiếp xúc với luồng gió từ điều hoà, máy lạnh để không làm niêm mạc mũi xoang bị khô rát, tổn thương. Ngoài ra, có thể kết hợp thực hiện một số động tác làm ấm vùng mũi vào mỗi sáng: Chụp hai bàn tay vào hai bên vùng cánh mũi và miệng, vừa xoa xoa tay vừa tập thở ra hít vào trong vòng vài phút.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bảo vệ mũi khỏi những tác nhân gây kích ứng: Trong không khí chứa vô số tác nhân xấu có thể kích ứng niêm mạc mũi như hoá chất, khói thuốc lá, nấm mốc, vi khuẩn, khí thải, bụi,... Cần tránh và hạn chế những yếu tố này xâm nhập bằng cách đeo khẩu trang hoạt tính khi ra đường, khi làm việc trong môi trường ô nhiễm, độc hại.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thường xuyên vệ sinh vùng tai, mũi, họng: Nếu không được vệ sinh sạch sẽ, đường hô hấp trên sẽ trở thành môi tường sinh sống và phát triển của nhiều loại vi khuẩn ký sinh. Do đó, mỗi người cần vệ sinh răng miệng, họng thật tốt bằng cách dùng nước muối sinh lý súc họng và đánh răng hàng ngày trước và sau khi ngủ dậy, sau mỗi bữa. Khi bị viêm lợi, viêm chân răng, sâu răng, viêm amidan, viêm họng,... phải đi khám và điều trị tận gốc để bệnh không trở thành mãn tính, dễ gây viêm xoang về sau.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Sử dụng thuốc hợp lý: Không nên lạm dụng các loại thuốc xịt mũi, nhỏ mũi vì có thể dẫn tới việc nhờn thuốc hoặc lệ thuộc thuốc, nên nhỏ thuốc đúng liều để tránh tác dụng phụ. Ngoài ra, những bệnh nhân bị viêm xoang mãn tính nên dùng nước muối sinh lý nhỏ mũi và sớm đi khám ngay khi có biểu hiện nghi ngờ là do viêm đường hô hấp, viêm xoang, viêm mũi. Khi bị viêm mũi, viêm xoang, có thể xông mũi bằng nước muối, nước ấm, nước pha chút tinh dầu bạc hà,...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Uống nhiều nước: Một cách phòng tránh bệnh&nbsp;<b>viêm mũi dị ứng</b>&nbsp;khác là uống nhiều nước nhằm làm loãng chất tiết nhày, từ đó giúp dịch tiết mũi lỏng hơn và chất nhày thoát ra ngoài dễ dàng hơn, không gây ứ đọng dẫn tới viêm nhiễm.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_091758_766047_trang1.max-1800x1800.jpg\" alt=\"vicare.vn-cach-phong-tranh-benh-viem-mui-di-ung-body-3\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Giải mẫn cảm đặc hiệu</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Cách này được bác sĩ chỉ định khi người bệnh không thể kiểm soát môi trường sống và việc điều trị bằng thuốc không có tác dụng. Sau khi thử test để xác định người bệnh dị ứng với kháng nguyên nào, bệnh nhân sẽ được nhỏ dưới lưỡi hoặc tiêm dưới da chất kháng nguyên gây bệnh với nồng độ tăng dần để giúp cơ thể thích nghi dần với chất đó và không bị dị ứng nữa.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Theo báo Thanh Niên, tỷ lệ thành công của phương pháp này lên tới 80 – 90%, có hiệu quả cao nhất với trường hợp&nbsp;<b>viêm mũi dị ứng</b>&nbsp;do lông chó mèo, bụi nhà và phấn hoa. Thời gian điều trị thường kéo dài từ 4 – 5 năm mới đạt hiệu quả như mong muốn và các triệu chứng chỉ bắt đầu cải thiện rõ rệt sau 6 – 12 tháng.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Những điều cần lưu ý về bệnh viêm mũi dị ứng</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Tốt nhất nên dùng thuốc để phòng ngừa những cơn dị ứng hơn là chữa trị hậu quả của phản ứng.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Nếu dùng corticoids, nên dùng dạng thuốc phun tại chỗ là an toàn nhất.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Với người bệnh bị dị ứng cần phải phẫu thuật, nên chữa dị ứng trước khi mổ để bảo đảm kết quả.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Phẫu thuật tuy không chữa được bệnh&nbsp;<b>viêm mũi dị ứng</b>&nbsp;nhưng nếu giải quyết được các lệch lạc trong cấu trúc mũi cũng rất có lợi, có thể giảm các triệu chứng của bệnh nhờ làm giảm tác nhân gây kích ứng.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_091904_879061_1_G.sized.max-1800x1800.jpg\" alt=\"vicare.vn-cach-phong-tranh-benh-viem-mui-di-ung-body-4\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cách điều trị tốt nhất là áp dụng những cách phòng tránh bệnh viêm mũi dị ứng như đã nói ở trên, tránh tiếp xúc với các dị nguyên gây bệnh.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Không phải cứ bị sổ mũi là viêm mũi dị ứng.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Chỉ dùng thuốc kháng sinh trong trường hợp bội nhiễm.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Nếu việc chữa trị cho một bệnh nhân dị ứng đang tiến triển tốt đột ngột không có hiệu quả, cần xem xét lại dị nguyên gây bệnh có bị thay đổi không, bị&nbsp;<b>viêm mũi dị ứng&nbsp;</b>do thuốc hay bị bội nhiễm.</p><p><br></p>', 0, NULL, '2017-12-14 13:42:53'),
(12, 9, 2, 'Bé bị viêm mũi nên uống thuốc gì?', 'post_I6mO.jpg', '<p><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">Không chỉ riêng viêm mũi mà với tất cả các loại bệnh, trẻ em luôn là đối tượng phải sử dụng thuốc đặc biệt vì cơ thể còn non yếu, các hệ cơ quan chưa phát triển hoàn chỉnh. Vậy,</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">bé bị viêm mũi nên uống thuốc gì</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;để vừa chữa được bệnh, vừa hạn chế tới mức thấp nhất những tác dụng phụ có thể ảnh hưởng đến sự phát triển cả về thể chất và tinh thần của trẻ?</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Bệnh viêm mũi rất thường gặp ở trẻ em, nhất là trẻ dưới 6 tuổi khi gặp những điều kiện thuận lợi như thời tiết giao mùa, trời đang nóng bỗng chuyển lạnh đột ngột. Viêm mũi có thể biểu hiện cấp hay mạn tính, gồm 2 loại chính là viêm mũi xuất tiết và viêm mũi mủ (viêm mũi do vi khuẩn).<b>&nbsp;Bé bị viêm mũi nên uống thuốc gì</b>&nbsp;sẽ phụ thuộc vào thể viêm mũi mà trẻ mắc phải và từng giai đoạn của bệnh.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Viêm mũi cấp xuất tiết</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Viêm mũi cấp xuất tiết chủ yếu do virus gây ra và có thể thành dịch. Trẻ bị viêm mũi xuất tiết thường có biểu hiện ngạt tắc mũi nên khó bú, chảy nước mũi trong, hắt hơi, sốt. Vậy,&nbsp;<b>bé bị viêm mũi nên uống thuốc gì&nbsp;</b>trong trường hợp này? Cách xử trị cụ thể sẽ theo phác đồ dưới đây:</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Toàn thân</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cho trẻ dùng thuốc hạ sốt paracetamol nếu trẻ sốt trên 38 độ C.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thuốc chống xuất tiết: Do virus được xem là kháng nguyên tác động lên mũi gây viêm nên có thể cho trẻ dùng kháng histamin H1 (có tác dụng phong bế các thụ thể H1 ngoại biên, từ đó ức chế phóng thích histamin từ dưỡng bào) với các thế hệ khác nhau như desloratidin, fexofenadin hydoclorid, loratidin, chlorpheniramin maleat,... Thuốc kháng histamin thường được dùng kéo dài khoảng 2 tuần trong mỗi đợt điều trị.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_082036_314173_girl-taking-medicin.max-1800x1800.jpg\" alt=\"vicare.vn-be-bi-viem-mui-nen-uong-thuoc-gi-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ngoài ra, có thể cho trẻ dùng kèm thuốc tăng cường chức năng miễn dịch (phổ biến nhất là thuốc chứa thymomodulin) làm tăng cường miễn dịch thông qua việc kích thích sự phát triển của tế bào Lympho B và Lympho T, cũng như kích thích đại thực bào tấn công những tác nhân gây bệnh, từ đó hoạt hóa bạch cầu T và tăng hoạt động tổng hợp kháng thể từ tế bào B làm tan rã những tác nhân gây bệnh ký sinh trong tế bào cơ thể.</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Tại chỗ</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thuốc làm săn khô niêm mạc mũi (nhóm muối bạc Argyrol).</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thuốc giảm phù nề, chống xung huyết niêm mạc mũi: Thuốc thông dụng hiện nay dùng cho trẻ bị viêm mũi (bao gồm cả trẻ sơ sinh) là xylomethazolin 0,05%, ngoài ra còn có ephedrine 0,1-0,3%, adrénaline 0,01%,...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Viêm mũi mạn tính</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Khác với viêm mũi xuất tiết, viêm mũi mạn tính là viêm mũi dị ứng hoạc viêm mũi mủ, thường đi kèm với viêm xoang. Trả lời câu hỏi khi&nbsp;<b>bé bị viêm mũi nên uống thuốc gì</b>, các bác sĩ cho biết trong trường hợp trẻ mắc viêm mũi mạn tính sẽ được cho dùng các loại thuốc sau:</p><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Toàn thân</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thuốc kháng sinh: Nuôi cấy dịch mũi làm kháng sinh đồ được xem là phương pháp điều trị viêm mũi tối ưu. Kháng sinh thường sử dụng khi bé bị viêm mũi mạn tính phải có phổ rộng để có thể tiêu diệt cả vi khuẩn gram dương, gram âm và vi khuẩn kỵ khí. Do đó, nên dùng nhóm cepholosporin thế hệ III.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Thuốc chống dị ứng: Cha mẹ nên cho trẻ dùng loại có chứa cellulose, micocrystalline,... theo chỉ định cho trẻ trên 6 tháng tuổi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"></p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_082109_587166_cham-soc-tre-bi-vie.max-1800x1800.jpg\" alt=\"vicare.vn-be-bi-viem-mui-nen-uong-thuoc-gi-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h3 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 22px;\">Tại chỗ</h3><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Thuốc chống viêm dạng nhỏ có corticoid như collydexa, polydexa,... dùng dưới 7 ngày. Thuốc chứa corticoid dùng cho mũi với khả năng chống viêm tại chỗ ở những liều không có tác dụng toàn thân. Trẻ từ 3 – 11 tuổi sử dụng điều trị dự phòng bằng nasonex nên dùng 2 – 4 tuần trước thời gian dự kiến bị viêm mũi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Hoặc phụ huynh có thể pha chế thuốc chữa viêm mũi thành dạng phun sương để dùng kéo dài trong 2 năm, tuy nhiên phải theo dõi chặt chẽ theo sự tiến triển hoặc suy thoái của bệnh để hiệu chỉnh liều lượng liên tục. Khi dùng kéo dài, trẻ phải được kiểm tra định kỳ để theo dõi nhưng thay đổi có thể xảy ra ở niêm mạc mũi. Nếu thấy có nhiễm nấm xuất hiện ở niêm mạc mũi họng phải ngưng điều trị ngay lập tức.</p><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Rửa mũi</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nếu chưa thể đi khám bác sĩ để biết chắc chắn được&nbsp;<b>bé bị viêm mũi nên uống thuốc gì</b>, phụ huynh có thể rửa mũi cho trẻ để giảm bớt hiện tượng thở khò khè, khó thở, thở bằng miệng khiến trẻ khó chịu. Tiện lợi và an toàn nhất là sử dụng nước muối NaCl 0,9% có bán tại các nhà thuốc để rửa mũi cho bé, nên rửa từ 3 – 4 lần/ngày tuỳ theo tình trạng nghẹt mũi của trẻ, nên rửa mũi cho trẻ trước khi bú hoặc ăn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Trước khi nhỏ, phụ huynh nên ngâm lọ nước muối vào trong nước ấm, sau đó nhỏ thử lên tay của mình rồi mới dùng cho trẻ. Khi rửa mũi, tiến hành rửa từng bên mũi chứ không rửa cùng lúc. Không nên dùng miệng hút mũi cho trẻ bởi nguy cơ lây lan mầm bệnh rất cao, thay vào đó có thể dùng dụng cụ hút mũi đã được tiệt trùng.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171214_082137_448414_ve-sinh-mui-cho-tre.max-1800x1800.jpg\" alt=\"vicare.vn-be-bi-viem-mui-nen-uong-thuoc-gi-body-3\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Lưu ý khác ngoài dùng thuốc</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Ngoài thắc mắc&nbsp;<b>bé bị viêm mũi nên uống thuốc gì</b>, cha mẹ cũng cần lưu ý một số điều sau trong quá trình phòng ngừa hoặc điều trị cho trẻ:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Vệ sinh môi trường sống xung quanh trẻ thật sạch sẽ để không làm bệnh tình của bé nặng hơn.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Phòng trẻ phải được giữ ấm nếu thười tiết lạnh, có thể đặt 1 chậu nước trong phòng trong những ngày trời quá hanh khô. Khi đi ra đường cần cho bé đeo khẩu trang và nên thay khẩu trang thường xuyên.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cách ly trẻ hoàn toàn khỏi khói thuốc lá.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Bổ sung đầy đủ chất dinh dưỡng và nước để tăng sức đề kháng cho trẻ.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Cần đưa trẻ tới các cơ sở y tế để được bác sĩ thăm khám và kê đơn phù hợp với bệnh tình cũng như độ tuổi của trẻ, tuyệt đối không tự ý mua thuốc về cho trẻ uống.</p><p><br></p>', 10, NULL, '2017-12-16 15:02:25');
INSERT INTO `post` (`id`, `userId`, `categoryId`, `name`, `avatar`, `content`, `views`, `unsigned_name`, `createdAt`) VALUES
(14, 8, 2, 'Bị khó thở khi mắc lao phổi cần làm gì?', 'post_q3ej.jpg', '<span style=\"color: rgb(51, 51, 51); font-size: 14px;\">Bệnh lao phổi là một loại bệnh được xếp vào danh sách các bệnh xã hội nguy hiểm bởi nó có khả năng lây nhiễm cực kỳ cao. Bệnh thường gây ra những cơn ho dai dẳng và khó thở khiến người bệnh cảm thấy mệt mỏi. Vậy&nbsp;</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">khó thở khi mắc lao phổi cần làm gì&nbsp;</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">là điều mà nhiều bệnh nhân quan tâm. Bài viết dưới đây&nbsp;</span><b style=\"color: rgb(51, 51, 51); font-size: 14px;\">ViCare</b><span style=\"color: rgb(51, 51, 51); font-size: 14px;\">&nbsp;sẽ cùng bạn giải quyết vấn đề trên.</span><br><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Nguyên nhân gây bệnh lao phổi<i></i></h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\"><b>Bệnh lao phổi&nbsp;</b>do vi khuẩn MTB (Mycobacterim tuberculosis) gây nên. Đây là một loại vi khuẩn có khả năng tồn tại trong không khí và lây nhiễm bệnh từ người này sang người khác qua đường hô hấp.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Làm việc trong môi trường bị ô nhiễm, có nhiều khói bụi, ẩm ướt sẽ khiến bạn có nguy cơ mắc bệnh lao phổi nhiều hơn so với những người khác.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Người bình thường nếu tiếp xúc thường xuyên với người bị bệnh lao hoặc các loại chất thải chứa vi khuẩn lao cũng có khả năng lây nhiễm bệnh lao phổi. Bệnh lao phổi có thể lây truyền qua không khí nên chỉ cần người bệnh hắt hơi, ho, nói chuyện, bắt tay, ngồi ăn chung,... là vi khuẩn lao cũng sẽ dễ dàng xâm nhập vào cơ thể bạn và gây ra&nbsp;<b>bệnh lao phổi</b>.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Ngoài ra, việc sử dụng các loại thịt từ các con vật nuôi bị nhiễm khuẩn lao cũng là nguyên nhân gây bệnh lao ở phổi ở người.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171215_141434_712959_20161226_054203_889.max-1800x1800.jpg\" alt=\"vicare.vn-bi-kho-tho-khi-mac-lao-phoi-can-lam-gi-body-1\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Các triệu chứng của bệnh lao phổi</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Bệnh lao phổi có những triệu chứng vô cùng rõ ràng. Nếu có những dấu hiệu sau đây thì bạn nên đến gặp bác sĩ để được khám và điều trị ngay trước khi bệnh trở nên nguy hiểm:</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ho là triệu chứng đầu tiên dễ nhận biết của người bị bệnh phổi. Đặc biệt nếu bệnh nhân ho dai dẳng trên ba tuần mà không phải do viêm phổi, viêm phế quản thì rất có thể là bệnh nhân đã bị nhiễm vi khuẩn lao.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ho ra máu: Có tới 60% những người bị lao phổi có biểu hiện là ho ra máu. Nguyên nhân là do phổi và phế quan bị tổn thương dẫn tới hiện tượng chảy máu trong đường hô hấp.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Khạc ra đờm: Khạc đờm là biểu hiện tăng dịch tiết do phổi và phế quản bị tổn thương những nếu người bệnh đã dùng thuốc kháng sinh một thời gian mà vẫn không có dấu hiệu thuyên giảm thì phải nghĩ đến bệnh lao phổi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Đau ngực, khó thở là triệu chứng tiếp theo của&nbsp;<b>bệnh lao phổi</b>&nbsp;mà người bệnh cần lưu ý. Ho nhiều sẽ gây ra tình trạng khó thở, đau ngực, gây khó khăn cho quá trình trao đổi khí.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Sốt cao hoặc sốt nhẹ bất thường vào buổi chiều. Nếu có kèm theo các triệu chứng như ho dai dẳng, khạc đờm, ho ra máu,... thì cần đến khám và làm các xét nghiệm lao phổi càng sớm càng tốt.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ra mồ hôi nhiều vào ban đêm cũng là một dấu hiệu cho thấy bạn có khả năng bị bệnh lao phổi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">- Ngoài ra, người bệnh lao còn có biểu hiện chán ăn, mệt mỏi, cơ thể suy nhược, da xanh xao và sút cân nghiêm trọng.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Bệnh lao phổi ngày nay đã có những phương pháp điều trị triệt để. Tuy nhiên nếu như người bệnh không đi khám chữa kịp thời thì bệnh lao phổi có thể gây ra những biến chứng nguy hiểm như tràn dịch màng phổi, dãn phế quản, suy hô hấp mãn tính, u nấm phổi,... Điều này sẽ khiến cho quá trình điều trị lao phổi trở nên phức tạp và mất thời gian hơn.</p><figure class=\"post-img-cover full\" style=\"margin-top: 10px; margin-bottom: 10px; float: left; width: 753.328px; text-align: center; color: rgb(51, 51, 51); font-size: 14px;\"><img src=\"https://dwbxi9io9o7ce.cloudfront.net/images/20171215_141504_713463_9c30fda60e00732c89a.max-1800x1800.jpg\" alt=\"vicare.vn-bi-kho-tho-khi-mac-lao-phoi-can-lam-gi-body-2\" class=\"full uploaded\" style=\"max-width: 100%; height: auto; margin-bottom: 16px; border-radius: 5px; width: 500px;\"></figure><h2 style=\"font-family: &quot;Palatino Linotype&quot;, &quot;Book Antiqua&quot;, Palatino, &quot;Times New Roman&quot;, Times, serif; font-weight: 900; line-height: 1.3; color: rgb(51, 51, 51); margin-top: 0px; font-size: 28px;\">Khó thở khi mắc lao phổi cần làm gì?</h2><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đau ngực khó thở là triệu chứng dễ nhận thấy khi bị bệnh lao phổi khiến người bệnh cảm thấy bị shock do không khí không di chuyển được vào phổi. Nếu không biết cách điều chỉnh có thể gây ra hiện tượng ngất xỉu, nhồi máu cơ tim,...</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Đầu tiên, nếu rơi vào tình trạng khó thở bạn hãy ngồi xuống và tựa đầu vào ghế, thành giường, hoặc nằm xuống, dùng tay vuốt nhẹ vào lồng ngực và phần lưng phía sau. Nên hít vào, thở ra một cách chậm dãi, đều đặn, để phổi có thể hoạt động ổn định trở lại.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Có thể mang theo thuốc bên mình hoặc để ở một nơi thuận tiện nhất mỗi khi bạn cần dùng đến. Khi bị<b>&nbsp;bệnh lao phổi,</b>&nbsp;bác sĩ sẽ kê cho bạn một vài loại thuốc chống lao thông dụng nhất như: Isoniazid, Rifampicin, Ethambutol, Pyrazinamid. Bạn sẽ cảm thấy tốt hơn sau khi uống thuốc.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Ngoài ra để giảm thiệu các&nbsp;<b>triệu chứng khó thở</b>&nbsp;do&nbsp;<b>bệnh lao phổi&nbsp;</b>gây ra bạn nên tăng cường ăn các loại thực phẩm như: rau xanh, hoa quả, các loại thịt, trứng gà, các loại đậu đỗ, ngũ cốc, gan động vật,... để điều trị chứng khó thở khi mắc bệnh lao phổi.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Tuyệt đối tránh xa các chất kích thích, các loại đồ uống có cồn, chè đặc, cafe, thuốc lá, thuốc lào.</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-size: 14px;\">Nên đeo khẩu trang khi đi ra ngoài. Bên cạnh đó, cần phải tránh xa những nơi có môi trường bị ô nhiễm, bụi bẩn. Hãy luôn chú ý theo dõi những triệu chứng của&nbsp;<b>bệnh lao phổi&nbsp;</b>và đi thăm khám bác sĩ thường xuyên để có phương án điều trị bệnh tốt nhất.</p><p></p><h2 style=\"font-family: \" palatino=\"\" linotype\",=\"\" \"book=\"\" antiqua\",=\"\" palatino,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" font-weight:=\"\" 900;=\"\" line-height:=\"\" 1.3;=\"\" color:=\"\" rgb(51,=\"\" 51,=\"\" 51);=\"\" margin-top:=\"\" 0px;=\"\" font-size:=\"\" 28px;\"=\"\"></h2>', 7, NULL, '2018-01-03 15:05:54');

